import './App.css';
import Header from "./MyComponents/Header";
import { Footer } from './MyComponents/Footer';
import { ToDos } from './MyComponents/ToDos';
import { AddTodo } from './MyComponents/AddTodo';
import React, {useState} from 'react';

function App() {
  const onDelete = (todo)=>{
    console.log("I am onDelete of todo", todo);
    setTodos(todos.filter((e)=>{
      return e!==todo;
    }
    ))
  }

  const addTodo = (title, desc)=>{
    let sno;
    if(todos.length==0){
      sno=0;
    }
    else{
      sno = todos[todos.length-1].sno+1;
    }
    const myTodo={
      sno: sno,
      title : title,
      desc: desc,
    }
    setTodos([...todos, myTodo]);
  }
  const [todos, setTodos] = useState([

    {
      sno: 1,
      title:"Water the Plants",
      desc: "You need to water the plants to impress mummy"
    },
    {
      sno: 2,
      title:"Water the Trees",
      desc: "You need to water the Trees to impress mummy"
    },
    {
      sno: 3,
      title:"Water the Shrubs",
      desc: "You need to water the Shrubs to impress mummy"
    },
    {
      sno: 4,
      title:"Water the Herbs",
      desc: "You need to water the Herbs to impress mummy"
    }
  ]);

  return (
    <>
   <Header title="My ToDos List" searchBar={true}/>
   <AddTodo addTodo={addTodo}/>
   <ToDos todos={todos} onDelete={onDelete}/>
   <Footer/>
    </>
  );
}

export default App;
